import React, { useState, useEffect } from 'react';
import moment from 'moment';
import './Calendar.css';

export default function Calendar() {
    const [date] = useState(moment());
    const [week, setWeek] = useState(date.week())
    const [daysInWeek, setDaysInWeek] = useState()
    const weekdaysShort = moment.weekdaysShort();

    const today = () => {
        return parseInt(date.format("D"));
    };



    useEffect(() => {
        const currentWeek = () => {
            const days = [];
            const startDate = date.startOf('week');
            for (let i = 0; i < 7; i++) {
                days.push(moment(startDate).add(i, 'day').format("D"));
            }
            setDaysInWeek(days);
        }
        currentWeek();
    }, [date])
    useEffect(() => {
        currentWeek()
    }, [])

    const nextWeek = (weekNum) => {
        const daysInWeek = [];
        const startDate = date.week(weekNum).add(1, 'weeks').startOf('week')
        for (let i = 0; i < 7; i++) {
            daysInWeek.push(moment(startDate).add(i, 'day'));
        }
        return daysInWeek;
    }

    const lastWeek = (weekNum) => {
        const daysInWeek = [];
        const startDate = date.week(weekNum).subtract(1, 'weeks').startOf('week');
        for (let i = 0; i < 7; i++) {
            daysInWeek.push(moment(startDate).add(i, 'day'));
        }
        return daysInWeek;
    }

    return (
        <div className="calendar">
            <table>
                <thead>
                    <tr>
                        <th colSpan="7" className="calendar-month">{"Week " + week}</th>
                    </tr>
                    <tr>
                        {weekdaysShort.map(day =>
                            <th key={day} className="week-day">
                                {day}
                            </th>
                        )}
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        {daysInWeek && daysInWeek.map((d, i) =>
                            <td key={i * 100}>{d}</td>)}
                    </tr>
                </tbody>
            </table>
        </div>
    )
}
