import React, { useState } from 'react'

export default function Form() {
    const [formControls, setFormControls] = useState({
        formControls: {
            email: {
                value: ''
            },
            name: {
                value: ''
            },
            password: {
                value: ''
            }
        }
    })

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setFormControls({
            formControls: {
                ...formControls,
                [name]: {
                    ...formControls[name],
                    value
                }
            }
        });
    }

    return (
        <div>
            <form>
                <p>Email:</p>
                <input type="email"
                    name="email"
                    value={email}
                    onChange={handleChange}
                />
            </form>
        </div>
    )
}
