    return (
        <div className="calendar">
            <table>
                <thead>
                    <tr>
                        <th colSpan="7" className="calendar-month">{"Week " + currentWeekNumber()}</th>
                    </tr>
                    <tr>
                        {weekdaysShort.map(day =>
                            <th key={day} className="week-day">
                                {day}
                            </th>
                        )}
                    </tr>
                </thead>
                <tbody>

                </tbody>
            </table>
        </div>
    )
}
