import React, {useState} from 'react';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import { makeStyles } from '@material-ui/core/styles';

import './TimeOptions.css';

const useStyles = makeStyles(theme => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    }
}));

export default function TimeOptions(props) {
    const classes = useStyles();
    const timeSlots = ["06:00-09:00", "09:00-12:00", "12:00-15:00", "15:00-18:00"];
    const { handleTime, selectedTime } = props;
    const [active, setActive] = useState(false);

    const handleClick = (e, time) => {
        const times = document.getElementsByTagName('time-slot');
        console.log(e.target, time)
        for (let i = 0; i < times.length; i++) {
            // Remove the class 'active' if it exists
            times[i].classList.remove('active')
            console.log(times[i])
        }
        // add 'active' classs to the element that was clicked
        e.target.classList.add('active')
    }

    return (
        <div>
            {/* {timeSlots.map((t, i) =>
                <div className="time-slot" key={i}>{t}</div>
            )} */}
            <div onClick={(e) => handleClick(e, "06:00-09:00")} className="time-slot">06:00-09:00</div>
            <div onClick={(e) => handleClick(e, "09:00-12:00")} className="time-slot">09:00-12:00</div>
            <div onClick={(e) => handleClick(e, "12:00-15:00")} className="time-slot">12:00-15:00</div>
            <div onClick={(e) => handleClick(e, "15:00-18:00")} className="time-slot">15:00-18:00</div>
            <FormControl className={classes.formControl}>
                <InputLabel>Time</InputLabel>
                <Select
                    native
                    value={selectedTime}
                    onChange={handleTime}
                >
                    <option value="" />
                    {timeSlots.map((t, i) =>
                        <option key={i} value={t}>{t}</option>
                    )}
                </Select>
            </FormControl>
        </div>
    )
}
