import React, { useState } from 'react'
import Input from './input/Input';
import TextField from '@material-ui/core/TextField';
import { Button } from '@material-ui/core';
import Times from '../times/Times';
import './Form.css';

export default function Form(props) {
    const {selectedDay} = props;
    const [selectedTime, setSelectedTime] = useState();
    const [formControls, setFormControls] = useState({
        email: {
            value: ''
        },
        name: {
            value: ''
        }
    })

    const handleChange = name => e => {
        console.log(e.target.name)
        const name = e.target.name;
        const value = e.target.value;
        setFormControls({
            ...formControls,
            [name]: {
                value: value
            }
        });
    }
    console.log(formControls)

    const handleBooking = (e) => {
        e.preventDefault()

        if(formControls.email.value && formControls.name.value && selectedTime){
            console.log(formControls.email.value, formControls.name.value, selectedTime)
        }
    }

    const handleTime = (e) => {
        setSelectedTime(e.target.value);
    }

    return (
        <div className="form-container">
            <form onSubmit={handleBooking}>
            <h4>Book a time slot for {selectedDay}:</h4>
            <Times handleTime={handleTime} selectedTime={selectedTime} />
            <Input
                label="Name"
                value={formControls.name.value}
                handleChange={handleChange}
            />
            <TextField
                className={classes.margin}
                label={props.label}
                value={props.value}
                onChange={props.handleChange}
                id="mui-theme-provider-standard-input"
            />
            <Button type="submit">Book</Button>
            </form>
        </div>
    )
}
