import React, { useState } from 'react';
import moment from 'moment';
import './Calendar.css';

export default function Calendar() {
    const [date] = useState(moment())
    const weekdaysShort = moment.weekdaysShort();

    const currentWeekNumber = () => {
        return date.week();
    };

    const today = () => {
        return parseInt(date.format("D"));
    };

    const currentWeek = () => {
        console.log(date.startOf('week'))
        console.log(date.endOf('week'))
    }

    const nextWeek = () => {
        console.log(moment().add(1, 'weeks').startOf('week'))
        console.log(moment().add(1, 'weeks').endOf('week'))
    }

    const generateCalendarBody = () => {
        let blanks = [];
        for (let i = 0; i < firstDayOfMonth(); i++) {
            blanks.push(
                <td key={i * 80} className="calendar-day empty">{""}</td>
            );
        }

        let daysInMonth = [];
        for (let d = 1; d <= numberOfDaysInMonth(); d++) {
            let currentDay = d === today() ? "today" : "";
            daysInMonth.push(
                <td key={d} className={`calendar-day ${currentDay}`}>
                    {d}
                </td>
            );
        }
        const totalSlots = [...blanks, ...daysInMonth];
        let rows = [];
        let cells = [];
        totalSlots.forEach((row, i) => {
            if (i % 7 !== 0) {
                cells.push(row); // if index not equal 7 that means not go to next week
            } else {
                rows.push(cells); // when reach next week we contain all td in last week to rows 
                cells = []; // empty container 
                cells.push(row); // in current loop we still push current row to new container
            }
            if (i === totalSlots.length - 1) { // when end loop we add remain date
                rows.push(cells);
            }
        });
        let totalDaysInMonth = rows.map((d, i) => {
            return <tr key={i * 100}>{d}</tr>;
        });
        return totalDaysInMonth
    }

    return (
        <div className="calendar">
            <table>
                <thead>
                    <tr>
                        <th colSpan="7" className="calendar-month">{month()}</th>
                    </tr>
                    <tr>
                        {weekdaysShort.map(day =>
                            <th key={day} className="week-day">
                                {day}
                            </th>
                        )}
                    </tr>
                </thead>
                <tbody>
                    {generateCalendarBody()}
                </tbody>
            </table>
        </div>
    )
}
