import React, { useState } from 'react'
import Input from './input/Input';
import { Button } from '@material-ui/core';
import TimeOptions from '../times/TimeOptions';
import './Form.css';

export default function Form(props) {
    const { selectedDay } = props;
    const [selectedTime, setSelectedTime] = useState();
    const [formControls, setFormControls] = useState({
        email: {
            value: ''
        },
        name: {
            value: ''
        }
    })

    const handleChange = name => (e) => {
        const value = e.target.value;
        setFormControls({
            ...formControls,
            [name]: {
                value: value
            }
        });
    }

    const handleBooking = (e) => {
        e.preventDefault()

        if (formControls.email.value && formControls.name.value && selectedTime && selectedDay) {
            console.log(formControls.email.value, formControls.name.value, selectedTime, selectedDay)
        }
    }

    const handleTime = (e) => {
        setSelectedTime(e.target.value);
    }

    return (
        <form onSubmit={handleBooking}>
            <h4>Book a time slot for {selectedDay}:</h4>
    const bookingSlots = ["06:00-09:00", "09:00-12:00","12:00-15:00","15:00-18:00"];

            <TimeOptions handleTime={handleTime} selectedTime={selectedTime} />
            <Input
                label="Name"
                value={formControls.name.value}
                handleChange={handleChange('name')}
            />
            <Input
                label="Email"
                value={formControls.email.value}
                handleChange={handleChange('email')}
            />
            <Button type="submit">Book</Button>
        </form>
    )
}
