import React, { useState, useEffect } from 'react';
import moment from 'moment';
import './Calendar.css';

export default function Calendar() {
    const [dateContext] = useState(moment());
    const [today] = useState(moment().format("D"));
    const [week, setWeek] = useState(date.week())
    const [daysInWeek, setDaysInWeek] = useState()
    const weekdaysShort = moment.weekdaysShort();

    useEffect(() => {
        const currentWeek = () => {
            const days = [];
            const startDate = dateContext.startOf('week');
            for (let i = 0; i < 7; i++) {
                days.push(moment(startDate).add(i, 'day').format("D"));
            }
            setDaysInWeek(days);
        }
        currentWeek();
    }, [dateContext])

    const nextWeek = (weekNum) => {
        const days = [];
        const startDate = dateContext.week(weekNum).add(1, 'weeks').startOf('week')
        for (let i = 0; i < 7; i++) {
            days.push(moment(startDate).add(i, 'day'));
        }
        return days;
    }

    const lastWeek = (weekNum) => {
        const days = [];
        const startDate = dateContext.week(weekNum).subtract(1, 'weeks').startOf('week');
        for (let i = 0; i < 7; i++) {
            days.push(moment(startDate).add(i, 'day'));
        }
        return days;
    }
    return (
        <div className="calendar">
            <table>
                <thead>
                    <tr>
                        <th colSpan="7" className="calendar-month">{"Week " + week}</th>
                    </tr>
                    <tr>
                        {weekdaysShort.map(day =>
                            <th key={day} className="week-day">
                                {day}
                            </th>
                        )}
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        {daysInWeek && daysInWeek.map((d, i) =>
                            <td className={d === today ? "calendar-day today" : "calendar-day"} key={i * 100}>{d}</td>)}
                    </tr>
                </tbody>
            </table>
        </div>
    )
}
